import React, { useState, useRef, useEffect } from 'react'
import music from '@/assets/music/music.mp3'
import './App.styl';

function App() {
  let [ rotated, setRotated ] = useState(0)
  // let [ stopAnimate, setStopAnimate ] = useState(false)
  let [ musicStyle, setMusicStyle ] = useState({})
  const audioRef = useRef(null)
  const animateFn = () => {
      this.requestAnimationObj = window.requestAnimationFrame(() => {
      // console.log(stopAnimate)
      // if (stopAnimate) return
      setMusicStyle({
        transform: `rotate(${rotated % 360}deg)`
      })
      setRotated(++rotated)
      animateFn()
    })
  }
  const onMusicPlay = () => {
    let audio = audioRef.current
    if (audio.paused) {
      audio.play().then(_ => {
        // setStopAnimate(false)
        animateFn()
      }).catch(e => {
        console.log(e)
      })
    } else {
      audio.pause()
      // setStopAnimate(true)
      console.log(this.requestAnimationObj)
      window.cancelAnimationFrame(this.requestAnimationObj)
      console.log(this.requestAnimationObj)
    }
  }

  useEffect(() => {
    audioRef.current.play()
  })
  return (
    <>
      <div className="lottery-container">
        <div className="lottery-music">
          <audio ref={audioRef} className="lottery-music__item" src={music}></audio>
          <div className="lottery-music__box" style={musicStyle} onClick={onMusicPlay}>Music</div>
        </div>
        <div className="lottery-prizeBar"></div>
        <div className="lottery-menu">
          <button>进入抽奖</button>
          <div className="lottery-none">
            <button>抽奖</button>
            <button>重新抽奖</button>
            <button>导出抽奖结果</button>
            <button>重置</button>
          </div>
        </div>
        <div className="lottery-bubble__container">
          <div className="lottery-bubble animated"></div>
        </div>
      </div>
    </>
  );
}

export default App;
