import React, { useState } from 'react'
import './App.styl';

function App() {
  const [ rotated, setRotated ] = useState(0)
  const [ stopAnimate, setStopAnimate ] = useState(false)
  const [ musicStyle, setMusicStyle ] = useState({})
  const animateFn = () => {
    window.requestAnimationFrame(() => {
      if (stopAnimate) return
      setRotated(rotated % 360)
      setMusicStyle({
        transform: `rotate(${rotated}deg)`
      })
    })
  }
  const onMusicPlay = (e) => {
    if (e.paused) {
      e.play()
    }
  }
  return (
    <>
      <div className="lottery-container">
        <div className="lottery-music">
          <audio className="lottery-music__item" src="@/assets/music/music.mp3"></audio>
          <div className="lottery-music__box" style={musicStyle} onClick={onMusicPlay}>Music</div>
        </div>
        <div className="lottery-prizeBar"></div>
        <div className="lottery-menu">
          <button>进入抽奖</button>
          <div className="lottery-none">
            <button>抽奖</button>
            <button>重新抽奖</button>
            <button>导出抽奖结果</button>
            <button>重置</button>
          </div>
        </div>
        <div className="lottery-bubble__container"></div>
      </div>
    </>
  );
}

export default App;
