import React, { useState, useRef, useEffect } from 'react'
import music from '@/assets/music/music.mp3'
import './App.styl';
import { prizeList } from '@/constant'

let stopAnimate = false
function App() {
  let [ rotated, setRotated ] = useState(0)
  let [ musicStyle, setMusicStyle ] = useState({})
  const audioRef = useRef(null)
  const animateFn = () => {
      window.requestAnimationFrame(() => {
      if (stopAnimate) return
      setMusicStyle({
        transform: `rotate(${rotated % 360}deg)`
      })
      setRotated(++rotated)
      animateFn()
    })
  }
  const onMusicPlay = () => {
    let audio = audioRef.current
    if (audio.paused) {
      audio.play().then(_ => {
        stopAnimate = false
        animateFn()
      }).catch(e => {
        console.log(e)
      })
    } else {
      audio.pause()
      stopAnimate = true
    }
  }

  useEffect(() => {
    audioRef.current.play()
  })
  return (
    <>
      <div className="lottery-container">
        <div className="lottery-music">
          <audio ref={audioRef} className="lottery-music__item" src={music}></audio>
          <div className="lottery-music__box" style={musicStyle} onClick={onMusicPlay}>Music</div>
        </div>
        <div className="lottery-prizeBar">
          <div class="lottery-prizeBar__title">
            正在抽取
            <label class="lottery-prizeBar__label">5等奖</label>
            <label class="lottery-prizeBar__label">凌美钢笔</label>
            ，剩余
            <label class="lottery-prizeBar__label">35</label>个
          </div>
          <ul className="lottery-prizeBar__list">
            {
              prizeList.map(prize => {
                return (
                  <li className="lottery-prizeBar__item">
                    <div className="lottery-prizeBar__img">
                      <img src={prize.img} alt={prize.title} />
                    </div>
                    <div className="lottery-prizeBar__text">
                      <h5 className="lottery-prizeBar__text-title">{ prize.type }等奖 { prize.title }</h5>
                      <div class="lottery-prizeBar__count">
                        <div class="progress">
                            <div class="progress-bar progress-bar-danger progress-bar-striped active" style={{width: '100%' }}></div>
                        </div>
                        <div class="lottery-prizeBar__count-left">
                            {prize.count + '/' + prize.count}
                        </div>
                    </div>
                    </div>
                  </li>
                )
              })
            }
          </ul>
        </div>
        <div className="lottery-menu">
          <button>进入抽奖</button>
          <div className="lottery-none">
            <button>抽奖</button>
            <button>重新抽奖</button>
            <button>导出抽奖结果</button>
            <button>重置</button>
          </div>
        </div>
        <div className="lottery-bubble__container">
          <div className="lottery-bubble animated"></div>
        </div>
      </div>
    </>
  );
}

export default App;
