import React, { useState, useRef } from 'react'
import './App.styl';

function App() {
  const [ state, setState ] = useState({
    time: 0,
    duration: 0,
    paused: true,
    muted: false,
    volumn: 1
  })
  const [ rotated, setRotated ] = useState(0)
  const [ stopAnimate, setStopAnimate ] = useState(false)
  const [ musicStyle, setMusicStyle ] = useState({})
  const audioRef = useRef(null)
  const animateFn = () => {
    window.requestAnimationFrame(() => {
      if (stopAnimate) return
      setRotated(rotated % 360)
      setMusicStyle({
        transform: `rotate(${rotated}deg)`
      })
    })
  }
  const onMusicPlay = () => {
    let audio = audioRef.current
    // console.log(audio)
    if (audio.paused) {
      try {
        audio.play()
        setStopAnimate(false)
        animateFn()
      } catch(e) {
        console.log(e)
      }
    }
  }
  return (
    <>
      <div className="lottery-container">
        <div className="lottery-music">
          <audio className="lottery-music__item" controls="controls" src="@/assets/music/music.mp3"></audio>
          <div ref={audioRef} className="lottery-music__box" style={musicStyle} onClick={onMusicPlay}>Music</div>
        </div>
        <div className="lottery-prizeBar"></div>
        <div className="lottery-menu">
          <button>进入抽奖</button>
          <div className="lottery-none">
            <button>抽奖</button>
            <button>重新抽奖</button>
            <button>导出抽奖结果</button>
            <button>重置</button>
          </div>
        </div>
        <div className="lottery-bubble__container">
          <div className="lottery-bubble animated"></div>
        </div>
      </div>
    </>
  );
}

export default App;
